import React, {useState} from "react";
import TextPicture from "../textpicture/textpicture";


export default function MenuItems({items}){



    return(
    items.map(item =>{
        return (
            <React.Fragment key={item.id}>
                <TextPicture 
                    text={item.name} pic='arrow.png' containerClass={item.isClicked? 'menu-item-2':'menu-item-2'}
                    textClass='menu-item-heading' picClass='menu-item-img'
                />
            </React.Fragment>
        )})
    )
}
