import React, {useState} from "react";
import './searchcolumn.css'
import MenuGrid from "./menugrid";



export default function SearchColumn(selectedOption){
    const [menuItems, setMenuItem] = useState([
        {
            name: 'Dim Sums',
            price: 'Rs 700',
            itemOf: 1
        },
        {
            name: 'Fried Rice',
            price: 'Rs 500',
            itemOf: 1
        },
        {
            name: 'Cheese',
            price: 'Rs 1500',
            itemOf: 2
        },
        {
            name: 'Fish',
            price: 'Rs 1500',
            itemOf: 2
        },
        {
            name: 'Fruit',
            price: 'Rs 2000',
            itemOf: 2
        },
        {
            name: 'MnMs',
            price: 'Rs 2000',
            itemOf: 2
        }

    ])
    return (
        <div className="search-items">
            <div className="search-header">
                <img src="search.png" className="search-img"/>
                <span className="search-heading">Search Item</span>
            </div>

            <div className="menu-grid">
                <MenuGrid key={'1'} selectedOption={selectedOption.selectedOption} menuItems={menuItems}/>
            </div>
        </div>
    )
}
