import React, {useState} from "react";
import TextPicture from "../textpicture/textpicture";


export default function MenuItems({items}){

    const selectItem = (e) => {
        console.log(e.currentTarget)
        e.currentTarget.querySelector().style.backgroundColor = '#5bbc79';
        e.currentTarget.style.backgroundColor = '#5bbc79';

        // console.log(item.id)
    }

    return(
    items.map(item =>{
        return (
            <React.Fragment key={item.id}>
                <TextPicture
                    customClickEvent={selectItem}
                    text={item.name} pic='arrow.png' 
                    containerClass='menu-item-1'
                    textClass= 'menu-item-heading-1'
                    picClass='menu-item-img-1'
                />
            </React.Fragment>
        )})
    )
}
