import './grandtotal.css'
document.getElementById('total').innerHTML=0;
let total_value=0;
function GrandTotal({myCart}) {

    myCart.map(item=>{
        total_value += (item.amount*item.price)
    })

    return (
        <div className="total-container">
            <div>Grand Total</div>
            <div id='total'></div>

        </div>
    )

}
document.getElementById('total').innerHTML=total_value;


export default GrandTotal;
