import React, {useState} from "react";
import TextPicture from "../textpicture/textpicture";


export default function MenuItems({items}){

    const [activeItem, setActiveItem] = useState({active: 1})

    return(
    items.map(item =>{
        return (
            <React.Fragment key={item.id}>
                if (activeItem.value==)
                <TextPicture text={item.name} pic='arrow.png' containerClass='menu-item-1' textClass='menu-item-heading' picClass='menu-item-img'/>
            </React.Fragment>
        )})
    )
}
