const increment = () => {
    return {
        type: 'INCREMENT_COUNT',
        payload: 'list'
    }
}

const decrement = (name) => {
    return {
        type: 'DECREMENT_COUNT',
        payload: {
            name: name
        }
    }
}

const addObjectToList = (name, price, amount=1) => {
    return {
        type: 'ADD_OBJ_TO_LIST',
        payload: {
            name: name,
            price: price,
            amount: amount
        }
    }
}

export default {
    increment, decrement, addObjectToList
}
