import React, {useState} from "react";
import GridItem from "./gridItem/gridItem";


export default function MenuGrid({selectedOption}){

    const [menuItems, setMenuItem] = useState([{
        '1': [{
            name: 'Dim Sums',
            price: 'Rs 700',
        },
        {
            name: 'Fried Rice',
            price: 'Rs 500',
        }]

    }])

    return (
        console.
        menuItems.map(items=>{

            if (items[selectedOption]){
                for(let x=0; x<items[selectedOption].length; x++ ){
                    return (<GridItem name={items[selectedOption][x].name} price={items[selectedOption][x].price} />)
                }
            }

        })
    )
}
