import React, {useEffect, useState} from "react";
import Categories from '../categories/categories.js'
import SearchColumn from "../searchcolumn/searchcolumn.js";
import './bodypage.css'
import Cart from "../cart/cart.js";
import axios from "axios";
import listReducer from '../../myRedux/reducers.js'
import {Provider} from 'react-redux'
import {legacy_createStore as createStore} from "redux";

let store = createStore(
    listReducer,
    window.__REDUX_DEVTOOLS_EXTENSION__ && window.__REDUX_DEVTOOLS_EXTENSION__()
)


export default function BodyPage(){
    const [selectedOption, setSelectedOption] = useState({option: 1})
    const [ingrdients, setIngrdients] = useState({})
    const [selectedMenu, setSelectedMenu] = useState({option: 0})
    const [menuItems, setMenuItem] = useState([
        {
            name: 'Dim Sums',
            price: 700,
            itemOf: 1,
            id: 1,
            amount: 0
        }
    ])
    const [myCart, setMyCart] = useState([])

    useEffect(()=>{
        if (selectedMenu>0) {
            let newObj;
            menuItems.map(item=>{
                if(item.id===selectedMenu){
                    newObj=item;  // Object that is changed
                    if(item.amount===0){  // Object which needs to be deleted from list
                        setMyCart(myCart.filter(x=>x!==item))
                        newObj=undefined
                    }
                }
            })
            if (newObj!==undefined){  // If there is no new object and cart doesnt already have obj then add to cart
                if (!myCart.includes(newObj)) setMyCart([...myCart, newObj])
            }
        }
    } , [menuItems]);

    async function getRequest(id){
        const response = await axios.get(
            `https://api.spoonacular.com/recipes/${id}/priceBreakdownWidget.json`,
            {
                params: {apiKey:'62d904eb869f495b8952b480d5bef8cf'}
            }
        );
        setIngrdients({data: response.data.ingredients})
    }

    useEffect(()=>{
        getRequest(selectedOption);
    },
    [selectedOption])

    useEffect(()=>{
        getRequest(1);
    },
    [])

    return(
        <Provider store={store}>
            <div className="menu-container">
                <Categories setSelectedOption={setSelectedOption}/>
                <SearchColumn 
                    selectedOption={selectedOption} ingrdients={ingrdients}
                />
                <Cart/>
            </div>
        </Provider>
    )
}
