const increment = () => {
    return {
        type: 'INCREMENT_COUNT',
        payload: 'list'
    }
}

const decrement = () => {
    return {
        type: 'DECREMENT_COUNT',
        payload: 'list'
    }
}

const addObjectToList = (name, price, amount=0) => {
    return {
        type: 'ADD_OBJ_TO_LIST',
        payload: {
            name: name,
            price: price,
            amount: amount
        }
    }
}

export default {
    addObjectToList,
    decrement, increment
}
