import React, {useState} from "react";
import GridItem from "./gridItem/gridItem";


export default function MenuGrid({selectedOption}){

    const [menuItems, setMenuItem] = useState([
        {
            name: 'Dim Sums',
            price: 'Rs 700',
            itemOf: 1
        },
        {
            name: 'Fried Rice',
            price: 'Rs 500',
            itemOf: 1
        },
        {
            name: 'Cheese',
            price: 'Rs 1500',
            itemOf: 2
        },
        {
            name: 'Fish',
            price: 'Rs 1500',
            itemOf: 2
        },
        {
            name: 'Fruit',
            price: 'Rs 2000',
            itemOf: 2
        },
        {
            name: 'MnMs',
            price: 'Rs 2000',
            itemOf: 2
        }

    ])

    return (
        menuItems.map(items=>{
            if (items.itemOf==[selectedOption]) return <GridItem name={items.name} price={items.price} />
        })
    )
}
