import React, {useState, useEffect} from "react";
import TextPicture from "../textpicture/textpicture";


export default function MenuItems({items}){

    const [myItems, setMyItems] = useState(items)

    const [defaultColor, setDefaultColor] = useState(items)

    useEffect(()=>{
        myItems.map(item=>{
            if (item.isClicked){
                // console.log(document.getElementById(item.id).querySelector("span"))
                document.getElementById(item.id).querySelector("span").style.backgroundColor = '#5bbc79';
                document.getElementById(item.id).querySelector("span").querySelector("span").style.color = '#ffffff';
            }
        })
    }, []);

    const selectItem = (e) => {

        const currentID = e.currentTarget.dataset.index
        function unselectDefault (currentID) {
            myItems.map(item=>{

                if (item.isClicked)
                if(item.id==currentID) item.isClicked = true
                else item.isClicked = false
                console.log(item) 
            })
            setMyItems(myItems)
        }
        unselectDefault (currentID)
        // console.log(e.target)
        // e.target.style.backgroundColor = '#5bbc79';
        // e.target.querySelector("span").style.color = '#ffffff';

    }

    return(
    items.map(item =>{
        return (
            <div key={item.id} data-index={item.id} onClick={selectItem} id={item.id}>
                <TextPicture
                    text={item.name} pic='arrow.png' 
                    containerClass='menu-item-1'
                    textClass= 'menu-item-heading-1'
                    picClass='menu-item-img-1'
                />
            </div>
        )})
    )
}
