import GridItem from "./gridItem/gridItem";


export default function MenuGrid({selectedOption}){

    const [menuItems, setMenuItem] = useState([{
        1: [{
            name: 'Dim Sums',
            price: 'Rs 700',
        },
        {
            name: 'Fried Rice',
            price: 'Rs 500',
        }]

    }])
    console.log(`Hi from menu grid :D ${selectedOption.option}`)
    return (
        menuItems.map(item=>{
            // console.log(`Hi from menu grid :O`)
            if (item.id===selectedOption.option){
                // console.log(`Hi from menu grid ${item.id}`)
                return (<GridItem name={item[item.id].name} price={item[item.id].price} />)
            }

        })
