        {
            name: 'Fruit',
            price: 'Rs 2000',
            itemOf: 2,
            id: 4,
            amount: 0
        },
        {
            name: 'MnMs',
            price: 'Rs 2000',
            itemOf: 2,
            id: 5,
            amount: 0
        }

    ])
    const [myCart, setMyCart] = useState([])

    useEffect(()=>{

        if (selectedMenu===null) console.log(`HI --> ${selectedMenu}`)
        //     console.log(menuItems[selectedMenu])

        // setMyCart(menuItems[selectedMenu])
    } , [menuItems]);

    return(
            <div className="menu-container">
                <Categories setSelectedOption={setSelectedOption}/>
