import React, {useState} from "react";
import TextPicture from "../textpicture/textpicture";


export default function MenuItems({items}){

    const selectItem = (e) => {
        console.log(e.currentTarget.id)
    }

    return(
    items.map(item =>{
        return (
            <React.Fragment key={item.id}>
                <TextPicture
                    customClickEvent={selectItem}
                    text={item.name} pic='arrow.png' 
                    containerClass={item.isClicked? 'menu-item-2':'menu-item-1'}
                    textClass={item.isClicked? 'menu-item-heading-2':'menu-item-heading-1'}
                    picClass='menu-item-img-1'
                />
            </React.Fragment>
        )})
    )
}
