import React, {useState} from "react";
import TextPicture from "../textpicture/textpicture";


export default function MenuItems({items}){

    const [myItems, setMyItems] = useState(items)

    const selectItem = (e) => {
        // const unselectDefault = () => {

        // }
        console.log('HI')
        console.log(e)
        e.currentTarget.style.backgroundColor = '#5bbc79';
        e.currentTarget.querySelector("span").style.color = '#ffffff';

    }

    return(
    items.map(item =>{
        return (
            <div key={item.id} onClick={selectItem}>
                <TextPicture
                    text={item.name} pic='arrow.png' 
                    containerClass='menu-item-1'
                    textClass= 'menu-item-heading-1'
                    picClass='menu-item-img-1'
                />
            </div>
        )})
    )
}
