import GridItem from "./gridItem/gridItem";


export default function MenuGrid({selectedOption}){

    const [menuItems, setMenuItem] = useState([
        {
            name: 'Dim Sums',
            price: 'Rs 700',
            itemOf: 1
        },
        {
            name: 'Fried Rice',
            price: 'Rs 500',
            itemOf: 1
        },
        {
            name: 'Cheese',
            price: 'Rs 1500',
            itemOf: 2
        }

    ])

    return (
        // console.log(menuItems)
        menuItems.map(items=>{

            if (items.itemOf==[selectedOption]){
                return (<GridItem name={items.name} price={items.price} />)

        //         for(let x=0; x<items[selectedOption].length; x++ ){
        //             
        //         }
            }

        })
    )
}
